mapper.insert:
insert into BOARDS(
    user_id,
    title,
    content
) values (
     #{userId},
     #{title},
     #{content}
 )

mapper.update:
update BOARDS
set title = #{title},
    content = #{content}
where board_no = #{boardNo}

